import { callIfFunction } from '../';
describe('callIfFunction', function () {
  it('returns a value if value is not a function', function () {
    expect(callIfFunction('x', [])).toBe('x');
  });
  it('returns an call of a function to arguments if value is a function', function () {
    expect(callIfFunction(function (x, y) {
      return x + y;
    }, 1, 2)).toBe(3);
  });
});