import { append } from 'ramda';
import composeC from '../composeC';
import rejectNil from '../rejectNil';
describe('composeC', function () {
  it('should provide curried compose', function () {
    var appendAndRejectNil = composeC(rejectNil, append);
    var appendCAndRejectNil = appendAndRejectNil('c');
    expect(appendCAndRejectNil(['a', null])).toEqual(['a', 'c']);
  });
});