import R from 'ramda';
import { dispatch } from '../';
describe('dispatch', function () {
  it('should return first not nil result', function () {
    expect(dispatch([R.always(null), R.identity, R.always(null)])(3)).toBe(3);
  });
  it('should be variadic', function () {
    expect(dispatch([R.always(null), function (a, b) {
      return a + b;
    }, R.always(null)])(3, 2)).toBe(5);
  });
  it('should return undefined', function () {
    expect(dispatch([R.always(null), R.identity, R.always(null)])(null)).toBe(undefined);
  });
});