import { dotPathOr } from '../';

var s = function s(x) {
  return JSON.stringify(x, null, 2);
};

describe('dotPathOr', function () {
  var itHelper = function itHelper(def, path, x, result) {
    return it("for path ".concat(s(path), ", default value ").concat(s(def), " object ").concat(s(x), " returns ").concat(result), function () {
      expect(dotPathOr(def)(path)(x)).toEqual(result);
    });
  };

  var def = 'N/A';
  itHelper(def, 'a.b', {}, def);
  itHelper(def, 'a.b', {
    a: {
      b: 1
    }
  }, 1);
  itHelper(def, 'a.c', {
    a: {
      c: 3
    }
  }, 3);
});