import { endsWithSuffixIgnoreCase } from '../';
describe('endsWithSuffixIgnoreCase', function () {
  it('should be defined', function () {
    expect(endsWithSuffixIgnoreCase).toBeDefined();
  });
  describe('when two equal strings are passed', function () {
    it('should return true', function () {
      expect(endsWithSuffixIgnoreCase('a', 'a')).toBe(true);
    });
  });
  it('should support currying', function () {
    var endsWithA = endsWithSuffixIgnoreCase('a');
    expect(endsWithA('a')).toBe(true);
  });
  describe('when two different strings are passed', function () {
    it('should return false', function () {
      expect(endsWithSuffixIgnoreCase('a', 'b')).toBe(false);
    });
    describe('when second string ends with the first', function () {
      it('should return true', function () {
        expect(endsWithSuffixIgnoreCase('a', 'ramda')).toBe(true);
      });
      describe('when one is upper case and second lower case', function () {
        it('should return true', function () {
          expect(endsWithSuffixIgnoreCase('A', 'ramda')).toBe(true);
        });
      });
    });
  });
});