import { equalsLength } from '../';
describe('equalsLength', function () {
  it('[{}] should equals one', function () {
    expect(equalsLength(1, [{}])).toBeTruthy();
  });
  it('[] should equal zero', function () {
    expect(equalsLength(0, [])).toBeTruthy();
  });
  it('[] should not equal one', function () {
    expect(equalsLength(1, [])).toBeFalsy();
  });
  it('should support curry', function () {
    var lengthEqualsOne = equalsLength(1);
    expect(lengthEqualsOne([{}])).toBeTruthy();
  });
});