import { equalsStringIgnoreCase } from '../';
describe('equalsStringIgnoreCase', function () {
  it('should be defined', function () {
    expect(equalsStringIgnoreCase).toBeDefined();
  });
  describe('when two equal strings are passed', function () {
    it('should return true', function () {
      expect(equalsStringIgnoreCase('a', 'a')).toBe(true);
    });
  });
  it('should support currying', function () {
    var equalsToA = equalsStringIgnoreCase('a');
    expect(equalsToA('a')).toBe(true);
  });
  describe('when two different strings are passed', function () {
    it('should return false', function () {
      expect(equalsStringIgnoreCase('a', 'b')).toBe(false);
    });
    describe('when one is upper case and second lower case of the same string', function () {
      it('should return true', function () {
        expect(equalsStringIgnoreCase('RAMDA', 'ramda')).toBe(true);
      });
    });
  });
});