import { flattenArgs } from '../';
describe('flattenArgs', function () {
  it('should exists', function () {
    expect(flattenArgs).toBeDefined();
  });
  it('should return array of arguments', function () {
    expect(flattenArgs('one', 'two')).toEqual(['one', 'two']);
  });
  it('should return flatten array of arguments', function () {
    expect(flattenArgs('one', ['two', 'tree'])).toEqual(['one', 'two', 'tree']);
  });
  it('should return flatten array from nested array arguments', function () {
    expect(flattenArgs('one', ['two', ['tree', 'four'], 'five'])).toEqual(['one', 'two', 'tree', 'four', 'five']);
  });
});