import flattenValues from '../flattenValues';
describe('flattenValues', function () {
  it('should recursively flatten values', function () {
    expect(flattenValues(['hi', {
      foo: 'bar'
    }, {
      foo: 'hi'
    }, {
      foo: {
        bar: ['cuuus', {
          foo: 'hi'
        }]
      }
    }, ['haha', 'hi'], ['haha', {
      foo: 'hi'
    }]])).toEqual(['hi', 'bar', 'hi', 'cuuus', 'hi', 'haha', 'hi', 'haha', 'hi']);
  });
});