import { all } from 'ramda';
import { isBoolean, equalsToTrue, equalsToFalse } from '../';
describe('isBoolean', function () {
  it('should exist', function () {
    expect(isBoolean).toBeTruthy();
  });
  it('returns true for Boolean type', function () {
    expect(all(equalsToTrue, [isBoolean(true), isBoolean(false)])).toBeTruthy();
  });
  it('returns false for non-Boolean type', function () {
    expect(all(equalsToFalse, [isBoolean(1), isBoolean(0), isBoolean(''), isBoolean('true'), isBoolean('false'), isBoolean(function () {}), isBoolean(NaN), isBoolean(null), isBoolean(undefined), isBoolean({}), isBoolean([])])).toBeTruthy();
  });
});