import { all, equals } from 'ramda';
import { isNumber } from '../';
describe('isNumber', function () {
  it('returns true for numbers', function () {
    expect(all(equals(true), [isNumber(Infinity), isNumber(NaN), isNumber(1), isNumber(1.1)])).toBeTruthy();
  });
  it('returns false for infinite numbers and non-number types', function () {
    expect(all(equals(false), [isNumber(''), isNumber(function () {}), isNumber(false), isNumber(null), isNumber(undefined), isNumber({}), isNumber([])])).toBeTruthy();
  });
});