import { lteThanLength } from '../';
describe('lteThanLength', function () {
  it('[{}] should be > than zero', function () {
    expect(lteThanLength(0, [{}])).toBeTruthy();
  });
  it('[] should be >= than zero', function () {
    expect(lteThanLength(0, [])).toBeTruthy();
  });
  it('[] should not be > than 1', function () {
    expect(lteThanLength(1, [])).toBeFalsy();
  });
  it('should support curry', function () {
    var lengthBiggerThanEqualsZero = lteThanLength(0);
    expect(lengthBiggerThanEqualsZero([{}])).toBeTruthy();
  });
});