import { mergeDeepLeftAll } from '../';
describe('mergeDeepLeftAll', function () {
  it('merges all objects in a list from the left', function () {
    var a = {
      fooA: {
        bar: 'a'
      },
      shared: {
        baz: 1
      }
    };
    var b = {
      fooB: {
        bar: 'b'
      },
      shared: {
        baz: 2
      }
    };
    var c = {
      fooC: {
        bar: 'c'
      },
      shared: {
        baz: 3
      }
    };
    expect(mergeDeepLeftAll([a, b, c])).toEqual({
      fooA: {
        bar: 'a'
      },
      fooB: {
        bar: 'b'
      },
      fooC: {
        bar: 'c'
      },
      shared: {
        baz: 1
      }
    });
  });
});