import R from 'ramda';
import { objOfOver } from '../';
describe('objOfOver', function () {
  it('returns result of over and set according to lens', function () {
    expect(objOfOver(R.lensPath(['a', 'b']), function (x) {
      return "Hello ".concat(x);
    }, {
      a: {
        b: 'foo'
      },
      c: 'bar'
    })).toEqual({
      a: {
        b: 'Hello foo'
      }
    });
  });
});