import { T } from 'ramda';
import { padLeftUntil } from '../';
describe('padLeftUntil', function () {
  it("returns '0000000001'", function () {
    expect(padLeftUntil(function (x) {
      return x.length === 10;
    }, '0')('1')).toBe('0000000001');
  });
  it("returns '1'", function () {
    expect(padLeftUntil(T, '')('1')).toBe('1');
  });
});