import { T } from 'ramda';
import { padRightUntil } from '../';
describe('padRightUntil', function () {
  it("returns '1000000000'", function () {
    expect(padRightUntil(function (x) {
      return x.length === 10;
    }, '0')('1')).toBe('1000000000');
  });
  it("returns '1'", function () {
    expect(padRightUntil(T, '')('1')).toBe('1');
  });
});