import { pathNotSatisfies } from '../';

var s = function s(x) {
  return JSON.stringify(x, null, 2);
};

describe('pathNotSatisfies', function () {
  var itHelper = function itHelper(pred, path, x, result) {
    return it("for path ".concat(s(path), ", value object ").concat(s(x), " returns ").concat(result), function () {
      expect(pathNotSatisfies(pred)(path)(x)).toEqual(result);
    });
  };

  var positive = function positive(x) {
    return x > 0;
  };

  itHelper(positive, ['a', 'b'], {}, true);
  itHelper(positive, ['a', 'b'], {
    a: {
      b: -2
    }
  }, true);
  itHelper(positive, ['a', 'b'], {
    a: {
      b: 3
    }
  }, false);
});