import { propNotEq } from '../';
describe('propNotEq', function () {
  it('should return true when object literal does not contain the property at all', function () {
    expect(propNotEq('a', 1, {})).toBeTruthy();
  });
  describe('when object literal contains the property', function () {
    it('should return true when the value of property differs', function () {
      expect(propNotEq('a', 1, {
        a: 2
      })).toBeTruthy();
    });
    it('should return false when the value of property equals', function () {
      expect(propNotEq('a', 1, {
        a: 1
      }));
    });
  });
});