import { propNotSatisfies } from '../';

var s = function s(x) {
  return JSON.stringify(x, null, 2);
};

describe('propNotSatisfies', function () {
  var itHelper = function itHelper(pred, prop, x, result) {
    return it("for prop ".concat(s(prop), ", value object ").concat(s(x), " returns ").concat(result), function () {
      expect(propNotSatisfies(pred)(prop)(x)).toEqual(result);
    });
  };

  var positive = function positive(x) {
    return x > 0;
  };

  itHelper(positive, 'a', {}, true);
  itHelper(positive, 'a', {
    a: -1
  }, true);
  itHelper(positive, 'a', {
    a: 3
  }, false);
});