import { rejectEq } from '../';
describe('rejectEq', function () {
  it('should exist', function () {
    expect(rejectEq).toBeDefined();
  });
  it('should filter values that equals to first argument', function () {
    expect(rejectEq('foo', ['foo', 'bar', 'foo', 'bar'])).toEqual(['bar', 'bar']);
  });
  it('should support currying', function () {
    var rejectFoo = rejectEq('foo');
    expect(rejectFoo(['foo', 'bar', 'foo', 'bar'])).toEqual(['bar', 'bar']);
  });
});