import { splitByNonAlphaNumeric } from '../';
describe('splitByNonAlphaNumeric', function () {
  describe('should split string by non-alphanumeric characters', function () {
    var splitByNonAlphaNumericUtil = function splitByNonAlphaNumericUtil(str, result) {
      return it("".concat(str, " to be ").concat(result), function () {
        return expect(splitByNonAlphaNumeric(str)).toEqual(result);
      });
    };

    splitByNonAlphaNumericUtil('h  e.l//l o wo.../r8****ld', ['h', 'e', 'l', 'l', 'o', 'wo', 'r8', 'ld']);
  });
});