import { startsWithPrefixIgnoreCase } from '../';
describe('startsWithPrefixIgnoreCase', function () {
  it('should be defined', function () {
    expect(startsWithPrefixIgnoreCase).toBeDefined();
  });
  describe('when two equal strings are passed', function () {
    it('should return true', function () {
      expect(startsWithPrefixIgnoreCase('a', 'a')).toBe(true);
    });
  });
  it('should support currying', function () {
    var startsWithA = startsWithPrefixIgnoreCase('a');
    expect(startsWithA('a')).toBe(true);
  });
  describe('when two different strings are passed', function () {
    it('should return false', function () {
      expect(startsWithPrefixIgnoreCase('a', 'b')).toBe(false);
    });
    describe('when second string starts with the first', function () {
      it('should return true', function () {
        expect(startsWithPrefixIgnoreCase('r', 'ramda')).toBe(true);
      });
      describe('when one is upper case and second lower case', function () {
        it('should return true', function () {
          expect(startsWithPrefixIgnoreCase('R', 'ramda')).toBe(true);
        });
      });
    });
  });
});