import { toCamelCase } from '../';
describe('toCamelCase', function () {
  describe('should convert string to cascalCase', function () {
    var toCamelCaseUtil = function toCamelCaseUtil(str, result) {
      return it("".concat(str, " to be ").concat(result), function () {
        return expect(toCamelCase(str)).toBe(result);
      });
    };

    toCamelCaseUtil('hello', 'hello');
    toCamelCaseUtil('hello-', 'hello');
    toCamelCaseUtil('   hello  ', 'hello');
    toCamelCaseUtil('hello world', 'helloWorld');
    toCamelCaseUtil('hello world AND univerSe', 'helloWorldANDUniverSe');
  });
});