import { toDotCase } from '../';
describe('toDotCase', function () {
  describe('should convert string to dot.case', function () {
    var toDotCaseUtil = function toDotCaseUtil(str, result) {
      return it("".concat(str, " to be ").concat(result), function () {
        return expect(toDotCase(str)).toBe(result);
      });
    };

    toDotCaseUtil('hello', 'hello');
    toDotCaseUtil('hello-', 'hello');
    toDotCaseUtil('   hello  ', 'hello');
    toDotCaseUtil('hello world', 'hello.world');
    toDotCaseUtil('hello world AND univerSe', 'hello.world.and.universe');
  });
});