import { toKebabCase } from '../';
describe('toKebabCase', function () {
  describe('should convert string to kebab-case', function () {
    var toKebabCaseUtil = function toKebabCaseUtil(str, result) {
      return it("".concat(str, " to be ").concat(result), function () {
        return expect(toKebabCase(str)).toBe(result);
      });
    };

    toKebabCaseUtil('hello', 'hello');
    toKebabCaseUtil('hello-', 'hello');
    toKebabCaseUtil('   hello  ', 'hello');
    toKebabCaseUtil('hello world', 'hello-world');
    toKebabCaseUtil('hello world AND univerSe', 'hello-world-and-universe');
  });
});