import { toPascalCase } from '../';
describe('toPascalCase', function () {
  describe('should convert string to PascalCase', function () {
    var toPascalCaseUtil = function toPascalCaseUtil(str, result) {
      return it("".concat(str, " to be ").concat(result), function () {
        return expect(toPascalCase(str)).toBe(result);
      });
    };

    toPascalCaseUtil('hello', 'Hello');
    toPascalCaseUtil('hello-', 'Hello');
    toPascalCaseUtil('   hello  ', 'Hello');
    toPascalCaseUtil('hello world', 'HelloWorld');
    toPascalCaseUtil('hello world AND univerSe', 'HelloWorldANDUniverSe');
  });
});