import { toScreamingSnakeCase } from '../';
describe('toScreamingSnakeCase', function () {
  describe('should convert string to SCREAMING_SNAKE_CASE', function () {
    var toScreamingSnakeCaseUtil = function toScreamingSnakeCaseUtil(str, result) {
      return it("".concat(str, " to be ").concat(result), function () {
        return expect(toScreamingSnakeCase(str)).toBe(result);
      });
    };

    toScreamingSnakeCaseUtil('hello', 'HELLO');
    toScreamingSnakeCaseUtil('hello-', 'HELLO');
    toScreamingSnakeCaseUtil('   hello  ', 'HELLO');
    toScreamingSnakeCaseUtil('hello world', 'HELLO_WORLD');
    toScreamingSnakeCaseUtil('hello world AND univerSe', 'HELLO_WORLD_AND_UNIVERSE');
  });
});