import { toSnakeCase } from '../';
describe('toSnakeCase', function () {
  describe('should convert string to snake_case', function () {
    var toSnakeCaseUtil = function toSnakeCaseUtil(str, result) {
      return it("".concat(str, " to be ").concat(result), function () {
        return expect(toSnakeCase(str)).toBe(result);
      });
    };

    toSnakeCaseUtil('hello', 'hello');
    toSnakeCaseUtil('hello-', 'hello');
    toSnakeCaseUtil('   hello  ', 'hello');
    toSnakeCaseUtil('hello world', 'hello_world');
    toSnakeCaseUtil('hello world AND univerSe', 'hello_world_and_universe');
  });
});